import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";

contract SwapIt is Ownable {
    using EnumerableSet for EnumerableSet.AddressSet;

    IUniswapV2Router02 private router;
    EnumerableSet.AddressSet private tokens;

    uint256 public feeRate = 950;

    constructor(address _uniswapV2Router) Ownable(msg.sender) {
        router = IUniswapV2Router02(_uniswapV2Router);
    }

    function setRouter(address _uniswapV2Router) public onlyOwner {
        router = IUniswapV2Router02(_uniswapV2Router);
    }

    function setFeeRate(uint256 _feeRate) public onlyOwner {
        require(_feeRate > 0 && _feeRate < 1000, "INVALID FEE RATE");
        feeRate = _feeRate;
    }

    function quote(
        uint256 amountA,
        uint256 reserveA,
        uint256 reserveB
    ) internal pure returns (uint256 amountB) {
        require(amountA > 0, "INSUFFICIENT AMOUNT");
        require(reserveA > 0 && reserveB > 0, "INSUFFICIENT LIQUIDITY");
        amountB = (amountA * reserveB) / reserveA;
    }

    function getAmountsOut(
        uint256 amountIn,
        address[] memory path
    ) public view returns (uint256) {
        uint256 amountIn2 = (amountIn * feeRate) / 1000;
        uint256[] memory amounts = router.getAmountsOut(amountIn2, path);
        return amounts[amounts.length - 1];
    }

    function getAmountsIn(
        uint256 amountOut,
        address[] memory path
    ) public view returns (uint256) {
        uint256[] memory amounts = router.getAmountsIn(amountOut, path);
        return amounts[amounts.length - 1];
    }

    function swapExactTokensForTokens(
        uint amountIn,
        uint amountOutMin,
        address[] calldata path,
        address to,
        uint deadline
    ) external {
        tokens.add(path[0]);
        IERC20(path[0]).transferFrom(msg.sender, address(this), amountIn);
        uint256 amountIn2 = (amountIn * feeRate) / 1000;
        IERC20(path[0]).approve(address(router), amountIn2);
        router.swapExactTokensForTokens(
            amountIn2,
            amountOutMin,
            path,
            to,
            deadline
        );
    }

    function swapExactTokensForETH(
        uint amountIn,
        uint amountOutMin,
        address[] calldata path,
        address to,
        uint deadline
    ) external {
        tokens.add(path[0]);
        IERC20(path[0]).transferFrom(msg.sender, address(this), amountIn);
        uint256 amountIn2 = (amountIn * feeRate) / 1000;
        IERC20(path[0]).approve(address(router), amountIn2);
        router.swapExactTokensForETH(
            amountIn2,
            amountOutMin,
            path,
            to,
            deadline
        );
    }

    function swapExactETHForTokens(
        uint amountOutMin,
        address[] calldata path,
        address to,
        uint deadline
    ) external payable {
        uint256 eth = (msg.value * feeRate) / 1000;
        router.swapExactETHForTokens{value: eth}(
            amountOutMin,
            path,
            to,
            deadline
        );
    }

    function withdrawAll() public onlyOwner {
        payable(owner()).transfer(address(this).balance);
        for (uint256 i; i < tokens.length(); i++) {
            IERC20 token = IERC20(tokens.at(i));
            token.transfer(owner(), token.balanceOf(address(this)));
        }
    }

    function getBalances()
        public
        view
        returns (address[] memory tokenAddresses, uint256[] memory balances)
    {
        uint256 len = tokens.length();
        tokenAddresses = new address[](len);
        balances = new uint256[](len);
        for (uint256 i; i < len; i++) {
            IERC20 token = IERC20(tokens.at(i));
            tokenAddresses[i] = address(token);
            balances[i] = token.balanceOf(address(this));
        }
    }
}
